# -*- coding: utf-8 -*- from .colors import colorf, COLORSfrom .datelib import *DEBUG=5INFO=4WARNING=3ERROR=2CRITICAL=1LEVEL_MSG={	DEBUG: 'DEBUG',	INFO: 'INFO',	WARNING: 'WARNING',	ERROR: 'ERROR',	CRITICAL: 'CRITICAL',}LEVEL_COLORS={	DEBUG: 'b_cyan',	INFO: 'b_blue',	WARNING: 'b_yellow',	ERROR: 'b_red',	CRITICAL: 'red',}FORMAT = "[%(date)s] %(color)s%(level)+8s: %(message)s%(nc)s"def setup(format):	global FORMAT	FORMAT = formatdef log(s, *args, **kwargs):		level_num = kwargs.get('level')	level_str = LEVEL_MSG.get(level_num)	level_color = LEVEL_COLORS.get(level_num)		if level_str is None:		level_num = INFO		level_str = LEVEL_MSG.get(level_num)		level_color = LEVEL_COLORS.get(level_num)		time = localtime()		params = {		'message': s,		'level': level_str,		'date': time.strftime(DATETIME_FORMAT),		'color': COLORS.get(level_color),		'nc': COLORS.get('nc'),	}	print (FORMAT % params)	def debug(s, *args, **kwargs):	log(s, *args, level=DEBUG, **kwargs)	def info(s, *args, **kwargs):	log(s, *args, level=INFO, **kwargs)	def warn(s, *args, **kwargs):	log(s, *args, level=WARNING, **kwargs)	def error(s, *args, **kwargs):	log(s, *args, level=ERROR, **kwargs)	def critical(s, *args, **kwargs):	log(s, *args, level=CRITICAL, **kwargs)